        const password = state.userPasswordInput
        const mydata = {
            "username": username,
            "password": password
        };
        const req = {
            method: "get",
            url: "http://0.0.0.0:5000/login?username="+username+"&password="+password
          };
          const self = this
          await axios(req)
          .then(function(response){
                console.log("token === ", response.data.token)
                console.log("password === ", password)
                console.log("login = ", store.is_login)
                if (response.data.token !== null ){
                    localStorage.setItem("token", response.data.token);
                    store.setState({token : response.data.token, 
                                    is_login : true, 
                                    username : state.userNameInput,
                                    password : state.userPasswordInput})
                    self.props.history.push("/")               
                    alert("login success")
                }
            })
            .catch(function(error){
                store.setState({isLoading: false})
                // handle error
                console.log(error)
