//import plugin
import React from 'react';
import { Link } from "react-router-dom";
import {store,actions} from '../store'
import {withRouter} from 'react-router-dom'
import {connect} from 'unistore/react'

//import style
import "../style/cart.css"
import Navbar from '../component/navbar';
import Category from '../component/navbarCategory'
import Search from '../component/search';
import CartFromSeller from '../component/cartFromSeller';

// untuk mengembalikan respon saat tidak ditemukan page yang sesuai
class Cart extends React.Component{
    componentDidMount = async () => {
        console.log("Didmount CART")
        await this.coba1()
        await this.coba2()
    }
    coba1 = async() => {
        await this.props.GetCart()
    }
    coba2 = async() => {

        await this.props.GetCartDetail(this.props.cartId)
        console.log("sudah axios ",this.props.cartId)
    }
    render(){
        const cart = this.props.cart;

        const ListCart = cart.filter(carts => {
            if (carts.id_user !== null && carts.id_seller !== null){
                console.log("aku true")
                return carts;
            }
            return false;
        });

        console.log("SAYA sudah di CART",ListCart)
        const ListCarts = ListCart.map((carts,key) => {
            console.log("Cart di LOOPING", carts)
            return(
                <CartFromSeller
                key={key}
                cartId = {carts.id}
                id_cart = {key.id}
                id_user_cart={carts.id_user}
                id_seller_cart={carts.id_seller}
                price_cart={carts.price_cart}
                />
            );
        });
        return(
            <nav>
                <Navbar/>
                <Search/>
                <div className="container-fluid cart-product">
                    <div className="row">
                        <div className="col-md-12 cart-direct">
                            <nav class="navbar navbar-expand-lg navbar-light bg-light" style={{paddingTop:"0",paddingLeft:"0",paddingBottom:"0",paddingRight:"0"}}>
                                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent2" aria-controls="navbarSupportedContent2" aria-expanded="false" aria-label="Toggle navigation">
                                    <span class="navbar-toggler-icon"></span>
                                </button>

                                <div class="collapse navbar-collapse" id="navbarSupportedContent2" style={{backgroundColor:"white",paddingLeft:"64%"}}>
                                    <ul class="navbar-nav mr-auto">
                                        <li class="nav-item">
                                            <Link to="/cart" class="nav-link" style={{color:"#9ACD32", fontSize:"35px"}}>Cart</Link>
                                        </li>
                                        <li class="nav-item">
                                            <p class="nav-link" style={{fontSize:"35px",fontWeight:"1000"}}>&#62;</p>
                                        </li>
                                        <li class="nav-item">
                                            <Link to="/store" class="nav-link" style={{color:"#9ACD32",fontSize:"35px"}}>Checkout</Link>
                                        </li>
                                        <li class="nav-item">
                                            <p class="nav-link" style={{fontSize:"35px",fontWeight:"1000"}}>&#62;</p>
                                        </li>
                                        <li class="nav-item">
                                            <p class="nav-link" style={{fontSize:"35px"}}>Finish</p>
                                        </li>
                                    </ul>
                                </div>
                            </nav>
                        </div>
                        {ListCarts}
                    </div>
                </div>
            </nav>
        )
    }
}

//mengeksport class NotMatch ke main-route.js
export default connect('cart', actions
)(withRouter(Cart));
