import createStore from 'unistore';
import { string } from 'prop-types';
import axios from 'axios';

const initialState = {
    userNameInput:"",
    userPasswordInput:"",
    UserFullName:"",
    userEmail:"",
    userAddress:"",
    userPhone:"",
    searchKeyword:"",
    isiArticle:"",
    judulArticle:"",
    fotoUrlInput: "",
    isLoading:false,
    cekFotoStatus: false,
    linkAudio: "",
    fullName : "",
    textAudio: "Indonesia",

};
export const store = createStore(initialState)

export const actions = store => ({
    getArticle: async (state, searchKeyword) => {
        console.log(initialState.searchKeyword)
        store.setState({isLoading:true})
        await axios
            .get("https://en.wikipedia.org/api/rest_v1/page/summary/"+searchKeyword)
            .then(function(response){
                store.setState({isiArticle:response.data.extract, judulArticle:response.data.title, isLoading:false})
                console.log(response)
                console.log(initialState.isiArticle)
            })
            .catch(function(error){
                store.setState({isLoading: false})
                console.log(initialState.searchKeyword)
                // handle error
                console.log(error)
            })
    },
    getRandomArticle: async (state) => {
        store.setState({isLoading:true})
        await axios
            .get("https://en.wikipedia.org/api/rest_v1/page/random/summary")
            .then(function(response){
                store.setState({isiArticle:response.data.extract, judulArticle:response.data.title, isLoading:false})
            })
            .catch(function(error){
                store.setState({isLoading: false})
                // handle error
                console.log(error)
            })
    },
    getSignIn : async (state) => {
        console.log("username",state.userNameInput)
        console.log("password",state.userPasswordInput)
        const username = state.userNameInput
        const password = state.userPasswordInput
        const mydata = {
            "username": username,
            "password": password
        };
        const req = {
            method: "get",
            url: "http://0.0.0.0:5000/login?username="+mydata.username+"&password="+mydata.password
          };

          await axios(req)
          .then(function(response){
                console.log("token === ", response.data.token)
                store.setState({token:response.data.token, is_login:true})
                console.log("token === ", this.state.token)
            .set('Authorization', 'Bearer ' + this.state.token)
            })
            .catch(function(error){
                store.setState({isLoading: false})
                // handle error
                console.log(error)
            })
    },

    postSignUp1 : async (state) => {
        const username = state.userNameInput
        const password = state.userPasswordInput
        const fullname = state.UserFullName
        const address = state.userAddress
        const email = state.userEmail
        const phone = state.userPhone
        const mydata = {
            "username": username,
            "password": password,
            "fullname": fullname,
            "email": email,
            "address": address,
            "number_phone": phone
        };

        const req = {
          method: "post",
          url: "http://0.0.0.0:5000/user/registration",
          headers: {
            "Content-Type": "application/json"
          },
          data: mydata
        };

        await axios(req)
            .then(response => {
                  store.setState({
                      "is_login": true
                  });
              //   return response
            })
            .catch(error => {
                console.log(mydata)
                console.log(error)
                return false
        })
    },
    postSignUp2 : async (state) => {
        console.warn("username",state.userNameInput)
        console.warn("password",state.userPasswordInput)
        console.warn("fullname",state.UserFullName)
        console.warn("address",state.userAddress)
        console.warn("email",state.userEmail)
