import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import "../style/shop.css"
import axios from 'axios';

class ListAllProduct extends Component{
    addToCart = async() =>{
        console.log("text cart",this.props.id_produk1)
        const mydata = {
            "id_product": this.props.id_produk1,
        };

        const req = {
          method: "post",
          url: "http://0.0.0.0:5000/user/cart",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.getItem("token")
          },
          data: mydata
        };

        await axios(req)
            .then(response => {
                  store.setState({
                      "is_login": true
                  });
              //   return response
            })
            .catch(error => {
                console.log(mydata)
                console.log(error)
                return false
        })
    }

    render(){
        return(
        <div className="col-md-3 product">
            <div class="card" style="width: 18rem;">
                <img class="card-img-top" src={this.props.gambar_produk1} alt="Card image cap"/>
                <div class="card-body">
                    <p className="card-title name-product">{this.props.nama_produk1}</p>
                    <p className="title-rating">Rate</p>
                    <p className="weight-product">{this.props.berat_produk1} gr</p>
                    <p className="rating-product">({this.props.rating_produk1}/10)</p>
                    <p className="seller-product">Hedy</p>
                    <p className="stock">stock: </p>
                    <p className="stock-product">{this.props.stock_produk1}</p>
                    <div>
                        <p className="price">Rp. {this.props.harga_produk1} /</p>
                        <p className="reference">{this.props.bungkus_produk1}</p>
                    </div>
                </div>
                <div class="card-body">
                    <button type="button" class="btn btn-primary add-to-cart" onClick={this.addToCart}>Add to cart</button>
                </div>
            </div>
        </div>
        );
    }
}

export default connect("", actions)(withRouter(ListAllProduct));
