import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import "../style/shop.css"
import axios from 'axios';
import CartFromProduct from './cartFromProduct';

class CartFromSeller extends Component{
    componentDidMount = async () => {
        await this.coba2()
    }
    coba2 = async() => {
        console.log("sudah axios ",this.props.cartId)
        await this.props.GetCartDetail(this.props.cartId)
    }
    render(){
        const cartdetail = this.props.cartdetail;
        // const cartdetail_product = this.props.cartdetail_product
        // console.log("cartdetail_product: ",cartdetail_product)
        console.log("SAYA sudah di SELLER",cartdetail)
        const ListCartDetail = cartdetail.filter((cartdetails) => {
            if (cartdetails.id_product !== null && cartdetails.quantity !== null){
                console.log("aku true")
                return cartdetails;
            }
            return false;
        });


        const ListCarts = ListCartDetail.map((cartdetails) => {
            // const cartdetail_product = this.props.cartdetail
            return(
                <CartFromProduct
                key={cartdetails.id}
                name_product_cart={cartdetails.id_product.name_product}
                weight_cart={cartdetails.id_product.weight}
                price_cart={cartdetails.id_product.price}
                reference_cart={cartdetails.id_product.reference}
                halal_cart={cartdetails.id_product.halal}
                image_cart={cartdetails.id_product.image}
                id_user_cart={cartdetails.id_user}
                id_seller_cart={cartdetails.id_seller}
                />
            );
        });

        return(
            <div className="col-md-12">
                <h1>Hedy</h1>
                {ListCarts}
            </div>
        );
    }
}

export default connect("cartdetail,cartdetail_product,carts", actions)(withRouter(CartFromSeller));
