import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import ModalSignIn from '../component/modalSignIn'
import ModalSignUp from '../component/modalSignUp'
import "../style/navbar.css"

class Search extends Component{
    choose = async(tastee) =>{
        console.log(this.props.category_taste,"ini kategori");
        await store.setState({ category_taste: tastee });  
        await this.props.history.push(`/listproduct/${this.props.category_taste}`)
    }

    render(){
        return(
                <nav>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"/>
                <nav class="navbar navbar-expand-lg navbar-light bg-light">
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent2" aria-controls="navbarSupportedContent2" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent2">
                <ul class="navbar-nav mr-auto">
                    <li class="nav-item">
                        <Link to="/listproduct" class="nav-link">300 Produk</Link>
                    </li>
                </ul>
                <ul class="navbar-nav ml-lg-5">
                    <div class="input-group mb-3">
                    <select class="custom-select" id="inputGroupSelect02" name='category_taste'>
                        <option selected disabled>Choose...</option>
                        <option value="sweet" onClick={() => this.choose("sweet")}>Sweet</option>
                        <option value="salty" onClick={() => this.choose("salty")}>Salty</option>
                        <option value="sour" onClick={() => this.choose("sour")}>Sour</option>
                        <option value="spicy" onClick={() => this.choose("spicy")}>Spicy</option>
                    </select>
                    <div class="input-group mb-3">
                    <select class="custom-select" id="inputGroupSelect02" name='category_halal'>
                        <option selected disabled>Choose...</option>
                        <option value="false" onClick={() => this.choose()}>No Halal</option>
                        <option value="true" onClick={() => this.choose()}>Halal</option>
                    </select>
                    <div class="input-group-append">
                        <input type="text" class="form-control" aria-label="Text input with segmented dropdown button"/></div>
                    </div>
                </div>
                </ul>
                </div>
            </nav>
            </nav>
        );
    }
}

export default connect("category_taste", actions)(withRouter(Search));
