import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import "../style/shop.css"
import axios from 'axios';
import CartFromProduct from './cartFromProduct';

class CartFromSeller extends Component{
    componentDidMount = async () => {
        console.warn("saya ada di sini kakak")
        await this.coba2()
    }
    coba2 = async() => {
        await this.props.GetCartDetail(this.props.carts.id)
    }
    render(){
        console.log("ini di render")
        const cartdetail = this.props.cartdetail;
        const cartdetail_product = this.props.cartdetail_product
        console.log("cart sudah di cart",cartdetail)
        const ListCartDetail = cartdetail.filter((cartdetails) => {
            if (cartdetails.id_product !== null && cartdetails.quantity !== null){
                console.log("aku true")
                return cartdetails;
            }
            return false;
        });

        const ListCarts = ListCartDetail.map((cartdetails) => {
            const cartdetail_product = this.props.cartdetail_product
            return(
                <CartFromProduct
                name_product_cart={cartdetail_product.name_product}
                name_product_cart={cartdetail_product.weight}
                name_product_cart={cartdetail_product.price}
                name_product_cart={cartdetail_product.name.reference}
                name_product_cart={cartdetail_product.halal}
                name_product_cart={cartdetail_product.image}
                id_user_cart={cartdetails.id_user}
                id_seller_cart={cartdetails.id_seller}
                />
            );
        });

        return(
            <div className="col-md-12">
                {ListCarts}
            </div>
        );
    }
}

export default connect("cartdetail,cartdetail_product", actions)(withRouter(CartFromSeller));
