import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import "../style/shop.css"
import axios from 'axios';
import CartFromProduct from './cartFromProduct';

class CartFromSeller extends Component{
    addToCart = async() =>{
        console.log("text cart",this.props.id_produk1)
        const mydata = {
            "id_product": this.props.id_produk1,
        };

        const req = {
          method: "post",
          url: "http://0.0.0.0:5000/user/cart",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.getItem("token")
          },
          data: mydata
        };

        await axios(req)
            .then(response => {
                  store.setState({
                      "is_login": true
                  });
              //   return response
            })
            .catch(error => {
                console.log(mydata)
                console.log(error)
                return false
        })
    }

    render(){
        return(
            <div className="col-md-12">
                <CartFromProduct/>
            </div>
        );
    }
}

export default connect("", actions)(withRouter(CartFromSeller));
