import React, {Component} from 'react';
// import "../style/css-final.css";
import "../style/bootstrap.min.css";
import { Link } from "react-router-dom";
// import Search from './search';
import { actions, store } from "../store";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import ModalSignIn from '../component/modalSignIn'
import ModalSignUp from '../component/modalSignUp'
import "../style/navbar.css"

class Navbar extends Component{
    add = async()=>{
        const role = await localStorage.getItem("role");
        const token = await localStorage.getItem("token");
        const username = await localStorage.getItem("username");
        console.log(this.token,this.username,this.role)
        return{role,token,username}
    }
    postSignout = () => {
        console.warn("nama username = ", this.props.username)
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        store.setState({"is_login": false});
        alert("logout succses!!!")
        // localStorage.clear()
        this.props.history.push("/");
    };
    render (){
        this.add()
        console.log(this.token,this.username,this.role)
        return(
                <header>
                <ModalSignUp/>
                <ModalSignIn/>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"/>
                <nav class="navbar navbar-expand-lg navbar-light bg-light">
                <Link to="/"><a class="navbar-brand navbar-logooo" href="#">E-mie</a></Link>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent1" aria-controls="navbarSupportedContent1" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent1">
                <ul class="navbar-nav mr-auto">
                    {/* {headerKategori} */}    
                    <li class="nav-item">
                        <Link to="/" class="nav-link">Home</Link>
                    </li>
                    {this.token !== null | this.token == undefined?
                    (<React.Fragment>
                        <li class="nav-item">
                            <Link to="/profile" class="nav-link">Profile</Link>
                        </li></React.Fragment>):
                    (
                    <React.Fragment></React.Fragment>)
                  }

                    <li class="nav-item">
                        <Link to="/blank" class="nav-link"> Post Article</Link>
                    </li>
                </ul>
                {/* <Search {...this.props}/> */}
                <ul class="navbar-nav ml-lg-5">
                  {this.token !== null | this.token == undefined ?
                    (<React.Fragment>
                        {(this.role === 'seller')?

                        <li class="nav-item">
                           <Link to="/Store" class="nav-link"> Add Product</Link>
                       </li>
                        :

                        <li class="nav-item">
                            <Link to="/Store" class="nav-link"> Store</Link>
                        </li>
                        }
                    <li class="nav-item">
                        <a className="nav-link">Hello, {this.username} |</a>
                    </li>
                    <li class="nav-item">
                        <Link class="nav-link" onClick={this.postSignout}>Logout</Link>
                    </li></React.Fragment>):
                    (
                    <React.Fragment>
                    <li class="nav-item">
                        <a class="nav-link" data-toggle="modal" data-target="#exampleModalCenter" type='button'>Login</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" data-toggle="modal" data-target="#exampleModalCenter1" type='button'>Sign Up</a>
                    </li></React.Fragment>)
                  }
                  <li class="nav-item">
                    <Link to="/blank" class="nav-link"><i class="fa fa-shopping-cart"></i></Link>
                  </li>
                </ul>
                </div>
            </nav> 
            </header>
        );
    }
}

export default connect("is_login, username", actions)(withRouter(Navbar));
